// Must include contact information:
// Email address
// Link to a PDF version of your CV
// Link to your GitHub profile
// Link to your LinkedIn page
// Must have a contact form for handling events


//Modal
import React from 'react';
import { Button, Modal } from 'react-bootstrap';
import cv from '../cv.pdf';

//Modal
class Example extends React.Component {
  constructor(props, context) {
    super(props, context);

    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);

    this.state = {
      show: false,
    };
  }

  handleClose() {
    this.setState({ show: false });
  }

  handleShow() {
    this.setState({ show: true });
  }

  render() {
    return (
      <>
        <a href="#" onClick={this.handleShow}>
          CV
        </a>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>CV</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <embed src={cv} type="application/pdf" width="100%" height="600px" />
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
}

function Contact() {
  return (
    <div className="contact">
      <h1>Contact</h1>
      <p><a href="mailto:michmatu@protonmail.com">e@mail</a>
      </p>
      <p>
        <Example />
      </p>
      <p>
        <a href="https://github.com/MichalMatu">GitHub</a>
      </p>
      <p>
        <a href="https://www.linkedin.com/in/michal-frontend/">LinkedIn</a>
      </p>
    </div>
  );
}

export default Contact;
